import React, { useContext, useEffect } from 'react';
import Link from 'next/link';
import {useRouter} from 'next/router';
import authContext from '../context/auth/authContext';
import appContext from '../context/app/appContext';

const Header = () => {
    
    // routing 
    const router = useRouter();

      // Extraer el Usuario autenticado del Storage 
    const AuthContext = useContext( authContext );
    const { usuario, usuarioAutenticado,  cerrarSesion } = AuthContext;

      // Context de la aplicación
    const AppContext = useContext( appContext );
    const { limpiarState } = AppContext;

    useEffect(() => {
        usuarioAutenticado()
    }, []);

    const redireccionar = () => {
        router.push('/');
        limpiarState();
    }



    return (
         <> 
         <nav className="border split-nav background">
            <div className="nav-brand">
            <div>
                 <button  onClick={() => redireccionar() }>Inicio</button>
               
             </div>
            </div>
            <div className="collapsible">
                 
             {
                    usuario ? (
                     
                     <>      
                            <p className="">Hola {usuario.nombre}</p>
                           <>
                            <button 
                                className="button button-text"
                                onClick={() => cerrarSesion() }
                            >Cerrar Sesión</button>
                              </>
                            </>
                      ) : (
               
    
              <div>
                  <Link href ="/login">
                  <button className="paper-btn  btn-danger">Iniciar sesion</button>
                  </Link>
                  <Link href ="/crear_cuenta">
                  <button className="paper-btn  btn-primary">Crear Cuenta</button>
                  </Link>
              </div>
                    )}

                </div>
          
         </nav>
          
    </>
     );
}
 
export default Header;
