import React, { useContext, useEffect } from 'react';
import Layout from '../Components/Layout'
import { useFormik} from 'formik';
import * as yup from 'yup';
import Alerta from '../components/Alerta';
import {useRouter} from 'next/router';

import authContext from '../context/auth/authContext';

const Login = () => {

    const AuthContext = useContext(authContext);
    const { mensaje, autenticado , iniciarSesionÂ } = AuthContext;

          
    //el routing

    const router = useRouter();

    useEffect(()=>{
           if (autenticado) {
                  router.push('/');           
           }
    },[autenticado]);
  
     //validacion con formik

     const formik = useFormik({
           
      //primero se le pasa un valor inicial

      initialValues:{
          email: '',
          pass:''
      },

      //evitar que se mande vacio el form con yup

      validationSchema: yup.object({
          email: yup.string().required('El correo es obligatorio')
          .email('el correo creado no es valido'),
          pass: yup.string().required('La clave es obligatoria')
          .min('La clave debe de ser de un minimo de 8 caracteres')
          

      }),
      onSubmit: valores => {
         iniciarSesion(valores)
      }

  })



  return ( 
    <Layout>

    <div className="text-center">

    { mensaje && <Alerta />}

    
               <h2 className="headline">Inicia sesion</h2>
           <form className="paper form-group" onSubmit={formik.handleSubmit}>
             
                 <input  type="email" placeholder="Escribe tu correo"
                value={formik.values.email}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                className="input-block"

               />

              { formik.touched.email && formik.errors.email ?(
                   <div className="alert alert-danger button-text">{formik.errors.pass} </div>
               ): null}
                 <input  type="password" placeholder="Escribe tu clave"
                value={formik.values.pass}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                className="input-block"
               />

               
              { formik.touched.pass && formik.errors.pass ?(
                   <div className="alert alert-danger button-text">{formik.errors.pass} </div>
               ): null}

               <button type="submit" className="second-button second-button-text btn-block"> Iniciar sesion</button>

           </form>

    </div>
  
</Layout> 
   );
}
 
export default Login;
