import React, { useContext, useEffect } from 'react';
import Layout from '../components/Layout';
import Alerta from '../components/Alerta';
import Dropzone from '../components/Dropzone';
import authContext from '../context/auth/authContext';
import appContext from '../context/app/appContext';
import Link from 'next/link';


const Index = () => {

  // se extrae el user autenticado desde el localstorage
  const AuthContext = useContext( authContext );
  const { usuarioAutenticado } = AuthContext;

  // Extraer el mensaje de error de archivos
  const AppContext = useContext( appContext );
  const { mensaje_archivo, url } = AppContext;

  useEffect(() => {
    const token = localStorage.getItem('token');

    if(token) {
      usuarioAutenticado()
    }

  }, []);

  return ( 
    <Layout>
        <div >
          {
          //URL
          url ? (
            <>
              <p className="">
                  <span className="">La URL:</span> {`${process.env.frontendURL}/enlaces/${url}`} 
              </p>
              <button 
                  className=""
                  onClick={
                    //FORMA DE COPIAR
                    () => navigator.clipboard.writeText(`${process.env.frontendURL}/enlaces/${url}`) }
              >Copiar Enlace</button>
            </>
          ) : (
            <>
            { mensaje_archivo && <Alerta /> }

              <div className="">
                  <Dropzone />

                  <div className="">
                      <h2 className="">Compartir archivos de forma sencilla y privada</h2> 
                      <p className="">
                        <span className="">SednImage</span> te permite compartir archivos con cifrado de extremo a extremo y un archivo que es eliminado después de ser descargado. 
                                  Así que puedes mantener lo que compartes en privado y asegurarte de que tus archivos no permanezcan en la internet para siempre.
                      </p>
                      <Link href="/crear_cuenta">
                          <a className="">Crea una cuenta para mayores beneficios</a>
                      </Link>

                  </div>
              </div>
            </>
          )}
        </div>
    </Layout>
   );
}

export default Index;
